import { motion, AnimatePresence } from "framer-motion";
import { useEffect, useState, useMemo } from "react";

interface CelebrationOverlayProps {}

interface ConfettiPiece {
  id: number;
  x: number;
  color: string;
  drift: number;
  delay: number;
  targetY: number;
}

export default function CelebrationOverlay({}: CelebrationOverlayProps) {
  const [windowHeight, setWindowHeight] = useState(800);

  useEffect(() => {
    setWindowHeight(window.innerHeight);
  }, []);

  // Memoize confetti data to prevent recreation on each render
  const confetti = useMemo<ConfettiPiece[]>(() => {
    return Array.from({ length: 25 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      color: ['#FFD700', '#FFA500', '#FF6347', '#32CD32', '#87CEEB'][Math.floor(Math.random() * 5)],
      drift: (Math.random() - 0.5) * 150,
      delay: Math.random() * 0.3,
      targetY: windowHeight + 50
    }));
  }, [windowHeight]);

  return (
    <motion.div
      className="fixed inset-0 pointer-events-none z-50"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      {/* Confetti */}
      {confetti.map((piece) => (
        <motion.div
          key={piece.id}
          className="absolute w-3 h-3 rounded-sm"
          style={{
            backgroundColor: piece.color,
            left: `${piece.x}%`,
            top: '-10px'
          }}
          animate={{
            y: [0, piece.targetY],
            rotate: [0, 360],
            x: [0, piece.drift]
          }}
          transition={{
            duration: 2.5,
            ease: "easeOut",
            delay: piece.delay
          }}
        />
      ))}

      {/* Balloons */}
      <div className="absolute bottom-0 left-0 right-0 flex justify-center pointer-events-none">
        {[...Array(5)].map((_, i) => (
          <motion.div
            key={i}
            className="text-6xl mx-4"
            initial={{ y: 100, opacity: 0 }}
            animate={{ 
              y: [-20, -40, -20],
              opacity: 1,
              rotate: [0, 5, -5, 0]
            }}
            transition={{
              duration: 2,
              repeat: 1,
              delay: i * 0.2,
              ease: "easeInOut"
            }}
            exit={{ y: -200, opacity: 0 }}
          >
            🎈
          </motion.div>
        ))}
      </div>

      {/* Fireworks */}
      <div className="absolute inset-0 flex items-center justify-center">
        {[...Array(3)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute text-8xl"
            style={{
              left: `${20 + i * 30}%`,
              top: `${20 + i * 20}%`
            }}
            initial={{ scale: 0, opacity: 0 }}
            animate={{
              scale: [0, 1.5, 0],
              opacity: [0, 1, 0],
              rotate: [0, 180]
            }}
            transition={{
              duration: 1.5,
              delay: i * 0.3,
              repeat: 1
            }}
          >
            ✨
          </motion.div>
        ))}
      </div>

      {/* Clapping Hands */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
        <motion.div
          className="text-8xl"
          animate={{
            scale: [1, 1.2, 1],
            rotate: [0, 10, -10, 0]
          }}
          transition={{
            duration: 0.5,
            repeat: 3
          }}
        >
          👏
        </motion.div>
      </div>
    </motion.div>
  );
}

client/src/components/LevelSelector.tsx.content:
import { motion } from "framer-motion";
import { useQuiz } from "../lib/stores/useQuiz";
import { Button } from "./ui/button";
import { Lock, Star } from "lucide-react";
import type { Level } from "../data/quizData";

export default function LevelSelector() {
  const { levels, unlockedLevels, selectLevel, getLevelScore } = useQuiz();

  return (
    <div className="max-w-4xl mx-auto py-8">
      <motion.h2 
        className="text-3xl md:text-4xl font-bold text-white text-center mb-8 drop-shadow-lg"
        initial={{ opacity: 0, y: -30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        🌟 Choose Your Adventure 🌟
      </motion.h2>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {levels.map((level: Level, index: number) => {
          const isUnlocked = unlockedLevels.includes(index);
          const score = getLevelScore(index);
          const hasCompleted = score !== null;

          return (
            <motion.div
              key={level.id}
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={isUnlocked ? { scale: 1.05 } : {}}
              className={`relative ${isUnlocked ? 'cursor-pointer' : 'cursor-not-allowed'}`}
            >
              <div className={`
                bg-white/20 backdrop-blur-sm rounded-3xl p-6 shadow-2xl border-2 transition-all duration-300
                ${isUnlocked 
                  ? 'border-yellow-300/50 hover:border-yellow-300/80 hover:bg-white/30' 
                  : 'border-gray-400/30 opacity-60'
                }
              `}>
                {/* Level Badge */}
                <div className="absolute -top-3 -right-3">
                  {hasCompleted ? (
                    <motion.div 
                      className="bg-green-500 rounded-full p-2 shadow-lg"
                      animate={{ rotate: [0, 10, -10, 0] }}
                      transition={{ duration: 2, repeat: Infinity }}
                    >
                      <Star className="text-white" size={20} fill="currentColor" />
                    </motion.div>
                  ) : !isUnlocked ? (
                    <div className="bg-gray-500 rounded-full p-2 shadow-lg">
                      <Lock className="text-white" size={20} />
                    </div>
                  ) : null}
                </div>

                {/* Level Content */}
                <div className="text-center">
                  <motion.div 
                    className="text-4xl mb-4"
                    animate={isUnlocked ? { rotate: [0, 5, -5, 0] } : {}}
                    transition={{ duration: 3, repeat: Infinity }}
                  >
                    {level.icon}
                  </motion.div>

                  <h3 className="text-xl font-bold text-white mb-2 drop-shadow-md">
                    {level.title}
                  </h3>

                  <p className="text-yellow-100 text-sm mb-4 line-clamp-2">
                    {level.description}
                  </p>

                  <div className="text-yellow-200 text-sm mb-4">
                    📝 {level.questions.length} Questions
                  </div>

                  {hasCompleted && (
                    <div className="text-green-300 text-sm mb-4">
                      🏆 Score: {score?.score}/{score?.total}
                    </div>
                  )}

                  <Button
                    onClick={() => isUnlocked && selectLevel(index)}
                    disabled={!isUnlocked}
                    size="lg"
                    className={`
                      w-full font-bold py-3 px-6 rounded-full text-lg shadow-lg transition-all duration-200
                      ${isUnlocked 
                        ? 'bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 text-white transform hover:scale-105' 
                        : 'bg-gray-400 text-gray-600 cursor-not-allowed'
                      }
                    `}
                  >
                    {hasCompleted ? '🔄 Play Again' : isUnlocked ? '🚀 Start Level' : '🔒 Locked'}
                  </Button>
                </div>
              </div>
            </motion.div>
          );
        })}
      </div>

      <motion.div 
        className="text-center mt-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1 }}
      >
        <p className="text-yellow-100 text-lg">
          Complete levels to unlock new adventures! 🌟
        </p>
      </motion.div>
    </div>
  );
}

