          whileTap={{ scale: 0.9 }}
        >
          {isMuted ? <VolumeX size={24} /> : <Volume2 size={24} />}
        </motion.button>
      </div>
    );
  }

  return (
    <div className="min-h-screen relative">
      {/* Header */}
      <motion.header 
        className="bg-white/10 backdrop-blur-sm border-b border-yellow-300/30 p-4"
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 100 }}
      >
        <div className="max-w-6xl mx-auto flex justify-between items-center">
          <motion.h1 
            className="text-2xl md:text-3xl font-bold text-white drop-shadow-lg"
            animate={{ 
              scale: [1, 1.01, 1]
            }}
            transition={{ duration: 4, repeat: 1 }}
            style={{
              textShadow: "0 0 15px rgba(255,215,0,0.8)"
            }}
          >
            🏆 Sikh Quiz History
          </motion.h1>

          <div className="flex gap-2">
            <motion.button
              onClick={goHome}
              className="bg-white/20 backdrop-blur-sm p-2 rounded-full text-white hover:bg-white/30 transition-all"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              <Home size={20} />
            </motion.button>

            <motion.button
              onClick={toggleMute}
              className="bg-white/20 backdrop-blur-sm p-2 rounded-full text-white hover:bg-white/30 transition-all"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
